    -0.009030 0.026488 1.182090 -0.502146;
    -0.703220 2.062739 -0.015179 2.564870;
    0.000000 0.000000 0.000000 1.000000];

J = (tMat*I)';
end

function polyBW = applyPolygons(J,polyBW)

% Define each manually generated ROI
hPoints(1).data = [-1.304052 -0.626324;
    -1.122811 -0.443071;
    -0.848739 -0.445482;
    -0.772485 -0.664904;
    -0.846529 -0.860214;
    -1.180277 -0.855392];

% Iteratively apply each ROI
